// Display static images on led matrix by pixelarray
void panel_display_static(uint16_t pixels[PANEL_X][PANEL_Y], bool overwrite = true) {

	if(overwrite)
		panel->clearScreen();

	for(int y = 0; y < PANEL_X; y++) {
		for(int x = 0; x < PANEL_Y; x++) {
			panel->drawPixel(x, y, pixels[y][x]);
		}
	}

	if(PANEL_DOUBLE_BUFFER)
		panel->flipDMABuffer();
}

// Initialize LED Matrix
void panel_setup() {

	HUB75_I2S_CFG mxconfig(
		PANEL_X,
		PANEL_Y,
		PANEL_CHAIN,
		{
			GPIO_LEDPANEL_R1,
			GPIO_LEDPANEL_G1,
			GPIO_LEDPANEL_B1,
			GPIO_LEDPANEL_R2,
			GPIO_LEDPANEL_G2,
			GPIO_LEDPANEL_B2,
			GPIO_LEDPANEL_A,
			GPIO_LEDPANEL_B,
			GPIO_LEDPANEL_C,
			GPIO_LEDPANEL_D,
			GPIO_LEDPANEL_E,
			GPIO_LEDPANEL_LAT,
			GPIO_LEDPANEL_OE,
			GPIO_LEDPANEL_CLK
		},
		PANEL_DRIVER,
		PANEL_DOUBLE_BUFFER,
		PANEL_I2C_SPEED,
		PANEL_LATCH_BLINK,
		PANEL_CLOCK_PHASE
	);

	// Display Setup
	panel = new MatrixPanel_I2S_DMA(mxconfig);
	panel->begin();
	panel->clearScreen();
	panel->setPanelBrightness(128); //TODO Add hardware and save in eeprom

	if(PANEL_DOUBLE_BUFFER)
		panel->flipDMABuffer();

}

